<script src="https://cdn.jsdelivr.net/npm/js-cookie@rc/dist/js.cookie.min.js"></script>
<script>
jQuery(document).ready(function () {
  if (!Cookies.get('covid19')) {
    jQuery("#cta-covid").modal();
  }

  jQuery('#covid19-hide').change(function () {
    if (jQuery('#covid19-hide').prop('checked')) {
      Cookies.set('covid19', 'hide');
    } else {
      Cookies.remove('covid19');
    }
  });

  jQuery(document).click(function(event) {
    if (!$(event.target).closest("#covid-modal").length) {
      $("body").find("#covid-modal").removeClass("show");
    }
  });
})
</script>

